// NiveisPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import NiveisList from '../components/Niveis/NiveisList';
import NiveisForm from '../components/Niveis/NiveisForm';

function NiveisPage() {
  const [niveis, setNiveis] = useState([]);

  useEffect(() => {
    const fetchNiveis = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/niveis');
        setNiveis(response.data);
      } catch (error) {
        console.error('Erro ao buscar os níveis:', error);
      }
    };

    fetchNiveis();
  }, []);

  const handleAddNivel = async (nivel) => {
    try {
      const response = await axios.post('http://localhost:3001/api/niveis', { nivel });
      setNiveis([...niveis, response.data]);
    } catch (error) {
      console.error('Erro ao adicionar o nível:', error);
    }
  };

  return (
    <div>
      <NiveisList niveis={niveis} />
      <NiveisForm onSubmit={handleAddNivel} />
    </div>
  );
}

export default NiveisPage;
