import React, { useState } from 'react';

function NiveisList({ niveis, onDelete }) {
  const [editedNivelId, setEditedNivelId] = useState(null);
  const [editedNivel, setEditedNivel] = useState('');

  const handleEdit = (id, nivel) => {
    setEditedNivelId(id);
    setEditedNivel(nivel);
  };

  const handleSave = (id) => {
    // Encontra o nível correspondente
    const editedIndex = niveis.findIndex((nivel) => nivel.id === id);
    if (editedIndex !== -1) {
      // Atualiza o nível com o valor editado
      const updatedNiveis = [...niveis];
      updatedNiveis[editedIndex] = { ...updatedNiveis[editedIndex], nivel: editedNivel };
      // Atualiza o estado dos níveis
      setNiveis(updatedNiveis);
    }
    // Limpa os campos de edição
    setEditedNivelId(null);
    setEditedNivel('');
  };

  const handleChange = (e) => {
    setEditedNivel(e.target.value);
  };

  return (
    <div>
      <h2>Lista de Níveis</h2>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nível</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {niveis.map((nivel) => (
            <tr key={nivel.id}>
              <td>{nivel.id}</td>
              <td>
                {editedNivelId === nivel.id ? (
                  <input
                    type="text"
                    value={editedNivel}
                    onChange={handleChange}
                  />
                ) : (
                  nivel.nivel
                )}
              </td>
              <td>
                {editedNivelId === nivel.id ? (
                  <button onClick={handleSave}>Salvar</button>
                ) : (
                  <>
                    <button onClick={() => handleEdit(nivel.id, nivel.nivel)}>Editar</button>
                    <button onClick={() => onDelete(nivel.id)}>Excluir</button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default NiveisList;
