import React, { useState } from 'react';

function NiveisList({ niveis, onDelete }) {
  const [editedNivel, setEditedNivel] = useState(null);

  const handleEdit = (nivel) => {
    setEditedNivel(nivel);
  };

  const handleSave = () => {
    // Verifica se há um nível editado
    if (editedNivel) {
      // Encontra o índice do nível editado no array de níveis
      const index = niveis.findIndex((n) => n.id === editedNivel.id);
      // Atualiza o array de níveis com o nível editado
      if (index !== -1) {
        const updatedNiveis = [...niveis];
        updatedNiveis[index] = editedNivel;
        // Atualiza o estado com os níveis editados
        setNiveis(updatedNiveis);
        // Limpa o estado editedNivel após salvar
        setEditedNivel(null);
      }
    }
  };

  const handleChange = (e) => {
    const { value } = e.target;
    // Atualiza o valor do nível editado
    setEditedNivel({ ...editedNivel, nivel: value });
  };

  return (
    <div>
      <h2>Lista de Níveis</h2>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nível</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {niveis.map((nivel) => (
            <tr key={nivel.id}>
              <td>{nivel.id}</td>
              <td>
                {editedNivel && editedNivel.id === nivel.id ? (
                  <input
                    type="text"
                    value={editedNivel.nivel}
                    onChange={handleChange}
                  />
                ) : (
                  nivel.nivel
                )}
              </td>
              <td>
                
                  <>
                    <button onClick={() => handleEdit(nivel)}>Editar</button>
                    <button onClick={() => onDelete(nivel.id)}>Excluir</button>
                  </>
                
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default NiveisList;
