import React, { useState, useEffect } from 'react';
import DesenvolvedoresList from '../components/Desenvolvedores/DesenvolvedoresList';
import DesenvolvedoresForm from '../components/Desenvolvedores/DesenvolvedoresForm';
import axios from 'axios';

function DesenvolvedoresPage() {
  const [desenvolvedores, setDesenvolvedores] = useState([]);

  const handleAddDesenvolvedor = async (desenvolvedor) => {
    try {
      const response = await axios.post('http://localhost:3002/api/desenvolvedores', desenvolvedor);
      const novoDesenvolvedor = response.data;
      setDesenvolvedores([...desenvolvedores, novoDesenvolvedor]);
    } catch (error) {
      console.error('Erro ao adicionar desenvolvedor:', error);
    }
  };

  useEffect(() => {
    const fetchDesenvolvedores = async () => {
      try {
        const response = await axios.get('http://localhost:3002/api/desenvolvedores');
        setDesenvolvedores(response.data);
      } catch (error) {
        console.error('Erro ao obter desenvolvedores:', error);
      }
    };
    fetchDesenvolvedores();
  }, []);

  return (
    <div>
      <DesenvolvedoresList desenvolvedores={desenvolvedores} />
      <DesenvolvedoresForm onSubmit={handleAddDesenvolvedor} />
    </div>
  );
}

export default DesenvolvedoresPage;
