import React, { useState } from 'react';

function NiveisList({ niveis, onEdit, onDelete }) {
  const [editedNivelId, setEditedNivelId] = useState(null);
  const [editedNivelValue, setEditedNivelValue] = useState('');

  const handleEdit = (id, value) => {
    setEditedNivelId(id);
    setEditedNivelValue(value);
  };

  const handleSave = (id) => {
    onEdit(id, editedNivelValue);
    setEditedNivelId(null);
    setEditedNivelValue('');
  };

  return (
    <div>
      <h2>Lista de Níveis</h2>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nível</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {niveis.map((nivel) => (
            <tr key={nivel.id}>
              <td>{nivel.id}</td>
              <td>
                {editedNivelId === nivel.id ? (
                  <input
                    type="text"
                    value={editedNivelValue}
                    onChange={(e) => setEditedNivelValue(e.target.value)}
                  />
                ) : (
                  nivel.nivel
                )}
              </td>
              <td>
                {editedNivelId === nivel.id ? (
                  <button onClick={() => handleSave(nivel.id)}>Salvar</button>
                ) : (
                  <button onClick={() => handleEdit(nivel.id, nivel.nivel)}>Editar</button>
                )}
                <button onClick={() => onDelete(nivel.id)}>Excluir</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default NiveisList;
