// src/components/NiveisForm.js
import React, { useEffect, useState } from 'react';

function NiveisForm({ onSubmit, editNivel }) {
  const [nivel, setNivel] = useState('');

  useEffect(() => {
    if (editNivel) {
      setNivel(editNivel.nivel);
    } else {
      setNivel('');
    }
  }, [editNivel]);

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(nivel);
    setNivel('');
  };

  return (
    <div className="max-w-md mx-auto mt-8">
      <h2 className="text-2xl font-semibold mb-4">{editNivel ? 'Editar Nível' : 'Cadastrar Nível'}</h2>
      <form onSubmit={handleSubmit} className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
        <div className="mb-4">
          <label htmlFor="nivel" className="block text-gray-700 text-sm font-bold mb-2">Nível:</label>
          <input
            type="text"
            id="nivel"
            name="nivel"
            value={nivel}
            onChange={(e) => setNivel(e.target.value)}
            required
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          />
        </div>
        <button type="submit" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
          {editNivel ? 'Atualizar' : 'Salvar'}
        </button>
      </form>
    </div>
  );
}

export default NiveisForm;
