import React, { useState } from 'react';

function NiveisList({ niveis, onEdit, onDelete }) {
  const [editedNivel, setEditedNivel] = useState(null);

  const handleEdit = (nivel) => {
    setEditedNivel(nivel);
  };

  const handleSave = () => {
    // Aqui você deve implementar a lógica para salvar o nível editado
    // Você pode chamar a função onEdit passando o nível editado como argumento
    // Exemplo: onEdit(editedNivel)
    // Lembre-se de limpar o estado editedNivel após salvar
    setEditedNivel(null);
  };

  const handleChange = (e) => {
    const { value } = e.target;
    setEditedNivel({ ...editedNivel, nivel: value });
  };

  return (
    <div>
      <h2>Lista de Níveis</h2>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nível</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {niveis.map((nivel) => (
            <tr key={nivel.id}>
              <td>{nivel.id}</td>
              <td>
                {editedNivel && editedNivel.id === nivel.id ? (
                  <input
                    type="text"
                    value={editedNivel.nivel}
                    onChange={handleChange}
                  />
                ) : (
                  nivel.nivel
                )}
              </td>
              <td>
                {editedNivel && editedNivel.id === nivel.id ? (
                  <button onClick={handleSave}>Salvar</button>
                ) : (
                  <>
                    <button onClick={() => handleEdit(nivel)}>Editar</button>
                    <button onClick={() => onDelete(nivel.id)}>Excluir</button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default NiveisList;
