import React, { useState } from 'react';

function DesenvolvedoresForm({ onSubmit, niveis }) {
  const [nome, setNome] = useState("");
  const [sexo, setSexo] = useState("M");
  const [dataNascimento, setDataNascimento] = useState("");
  const [hobby, setHobby] = useState("");
  const [nivelId, setNivelId] = useState(niveis.length > 0 ? niveis[0].id : "");

  const handleSubmit = async (e) => {
    e.preventDefault();
    const novoDesenvolvedor = {
      nome,
      sexo,
      dataNascimento,
      hobby,
      nivelId: parseInt(nivelId),
    };
    await onSubmit(novoDesenvolvedor);
    setNome("");
    setSexo("M");
    setDataNascimento("");
    setHobby("");
    setNivelId(niveis.length > 0 ? niveis[0].id : "");
  };

  return (
    <div className="w-full md:w-3/4 lg:w-1/2 mx-auto p-4 bg-gray-600 rounded-md shadow-md">
      <h1 className="text-2xl font-sans font-bold text-white mb-4">Cadastrar Desenvolvedor</h1>
      <form onSubmit={handleSubmit} className="flex flex-col items-center">
        <label htmlFor="nome" className="text-white mb-2 w-64 text-left">Nome:</label>
        <input type="text" id="nome" value={nome} onChange={(e) => setNome(e.target.value)} required className="px-4 py-2 mb-4 rounded-md shadow-sm bg-gray-700 text-white w-64" />
        
        <label htmlFor="sexo" className="text-white mb-2 w-64 text-left">Sexo:</label>
        <select id="sexo" value={sexo} onChange={(e) => setSexo(e.target.value)} required className="px-4 py-2 mb-4 rounded-md shadow-sm bg-gray-700 text-white w-64">
          <option value="M">M</option>
          <option value="F">F</option>
          <option value="Outro">Outro</option>
        </select>
        
        <label htmlFor="dataNascimento" className="text-white mb-2 w-64 text-left">Data de Nascimento:</label>
        <input type="date" id="dataNascimento" value={dataNascimento} onChange={(e) => setDataNascimento(e.target.value)} required className="px-4 py-2 mb-4 rounded-md shadow-sm bg-gray-700 text-white w-64" />
        
        <label htmlFor="hobby" className="text-white mb-2 w-64 text-left">Hobby:</label>
        <input type="text" id="hobby" value={hobby} onChange={(e) => setHobby(e.target.value)} required className="px-4 py-2 mb-4 rounded-md shadow-sm bg-gray-700 text-white w-64" />
        
        <label htmlFor="nivelId" className="text-white mb-2 w-64 text-left">NÃ­vel:</label>
        <select id="nivelId" value={nivelId} onChange={(e) => setNivelId(e.target.value)} required className="px-4 py-2 mb-4 rounded-md shadow-sm bg-gray-700 text-white w-64">
          {niveis.map(nivel => (
            <option key={nivel.id} value={nivel.id}>{nivel.nivel}</option>
          ))}
        </select>
        
        <button type="submit" className="bg-violet-500 hover:bg-violet-600 text-white px-4 py-2 rounded-md w-64">Salvar</button>
      </form>
    </div>
  );
}

export default DesenvolvedoresForm;
