import React, { useState } from 'react';

function NiveisList({ niveis, onEdit, onDelete }) {
  const [editedNivel, setEditedNivel] = useState({ id: null, nivel: '' });

  const handleEdit = (nivel) => {
    setEditedNivel({ id: nivel.id, nivel: nivel.nivel });
  };

  const handleSaveEdit = () => {
    onEdit(editedNivel.id, editedNivel.nivel);
    setEditedNivel({ id: null, nivel: '' });
  };

  return (
    <div>
      <h2>Lista de Níveis</h2>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nível</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {niveis.map((nivel) => (
            <tr key={nivel.id}>
              <td>{nivel.id}</td>
              <td>
                {editedNivel.id === nivel.id ? (
                  <input
                    type="text"
                    value={editedNivel.nivel}
                    onChange={(e) =>
                      setEditedNivel({ ...editedNivel, nivel: e.target.value })
                    }
                  />
                ) : (
                  nivel.nivel
                )}
              </td>
              <td>
                {editedNivel.id === nivel.id ? (
                  <button onClick={handleSaveEdit}>Salvar</button>
                ) : (
                  <button onClick={() => handleEdit(nivel)}>Editar</button>
                )}
                <button onClick={() => onDelete(nivel.id)}>Excluir</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default NiveisList;
