import React, { useState } from 'react';

function NiveisList({ niveis, onEdit, onDelete }) {
  const [editedValue, setEditedValue] = useState('');
  const [editIndex, setEditIndex] = useState(null);

  const handleEditChange = (event) => {
    setEditedValue(event.target.value);
  };

  const handleEditSubmit = (nivelId) => {
    onEdit(nivelId, editedValue);
    setEditIndex(null);
    setEditedValue('');
  };

  return (
    <div>
      <h2>Lista de Níveis</h2>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nível</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {niveis.map((nivel, index) => (
            <tr key={nivel.id}>
              <td>{nivel.id}</td>
              <td>
                {editIndex === index ? (
                  <input
                    type="text"
                    value={editedValue}
                    onChange={handleEditChange}
                  />
                ) : (
                  nivel.nivel
                )}
              </td>
              <td>
                {editIndex === index ? (
                  <button onClick={() => handleEditSubmit(nivel.id)}>Salvar</button>
                ) : (
                  <button onClick={() => { setEditIndex(index); setEditedValue(nivel.nivel); }}>Editar</button>
                )}
                <button onClick={() => onDelete(nivel.id)}>Excluir</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default NiveisList;
