import React, { useState, useEffect } from 'react';
import axios from 'axios';

function NiveisList({ niveis, onDelete }) {
  const [editedNivelId, setEditedNivelId] = useState(null);
  const [editedNivel, setEditedNivel] = useState('');
  const [niveisList, setNiveisList] = useState([]);

  useEffect(() => {
    setNiveisList(niveis);
  }, [niveis]);

  const handleEdit = (id, nivel) => {
    setEditedNivelId(id);
    setEditedNivel(nivel);
  };

  const handleSave = async (id) => {
    try {
      await axios.put(`http://localhost:3002/api/niveis/${id}`, { nivel: editedNivel });
      setEditedNivelId(null);
      setNiveisList((prevNiveis) =>
        prevNiveis.map((n) => (n.id === id ? { ...n, nivel: editedNivel } : n))
      );
    } catch (error) {
      console.error('Erro ao editar o nível:', error);
    }
  };

  const handleChange = (e) => {
    setEditedNivel(e.target.value);
  };

  return (
    <div className="w-full md:w-3/4 lg:w-1/2 mx-auto p-4 bg-gray-800 rounded-md shadow-md">
      <h2 className="text-2xl font-bold text-white mb-4">Lista de Níveis</h2>
      <table className="w-full">
        <thead>
          <tr>
            <th className="text-left text-white">ID</th>
            <th className="text-left text-white">Nível</th>
            <th className="text-left text-white">Ações</th>
          </tr>
        </thead>
        <tbody>
          {niveisList.map((nivel) => (
            <tr key={nivel.id}>
              <td className="text-white">{nivel.id}</td>
              <td>
                {editedNivelId === nivel.id ? (
                  <input
                    type="text"
                    value={editedNivel}
                    onChange={handleChange}
                    className="w-full px-4 py-2 border rounded-md shadow-sm bg-gray-700 text-white"
                  />
                ) : (
                  nivel.nivel
                )}
              </td>
              <td>
                {editedNivelId === nivel.id ? (
                  <button
                    onClick={() => handleSave(nivel.id)}
                    className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-md mr-2"
                  >
                    Salvar
                  </button>
                ) : (
                  <>
                    <button
                      onClick={() => handleEdit(nivel.id, nivel.nivel)}
                      className="bg-yellow-500 hover:bg-yellow-600 text-white px-4 py-2 rounded-md mr-2"
                    >
                      Editar
                    </button>
                    <button
                      onClick={() => onDelete(nivel.id)}
                      className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-md"
                    >
                      Excluir
                    </button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default NiveisList;
