// src/components/NiveisList.js
import React, { useState } from 'react';

function NiveisList({ niveis, onUpdate, onDelete }) {
  const [editId, setEditId] = useState(null);
  const [editValue, setEditValue] = useState('');

  const handleEditClick = (nivel) => {
    setEditId(nivel.id);
    setEditValue(nivel.nivel);
  };

  const handleSaveClick = (id) => {
    onUpdate(id, editValue);
    setEditId(null);
    setEditValue('');
  };

  return (
    <div className="max-w-xl mx-auto mt-8">
      <h2 className="text-2xl font-semibold mb-4">Lista de Níveis</h2>
      <table className="min-w-full bg-white border border-gray-200 rounded-lg">
        <thead>
          <tr>
            <th className="py-2 px-4 border-b">ID</th>
            <th className="py-2 px-4 border-b">Nível</th>
            <th className="py-2 px-4 border-b">Ações</th>
          </tr>
        </thead>
        <tbody>
          {niveis.map(nivel => (
            <tr key={nivel.id}>
              <td className="py-2 px-4 border-b">{nivel.id}</td>
              <td className="py-2 px-4 border-b">
                {editId === nivel.id ? (
                  <input 
                    type="text" 
                    value={editValue} 
                    onChange={(e) => setEditValue(e.target.value)}
                    className="w-full px-2 py-1 border rounded"
                  />
                ) : (
                  nivel.nivel
                )}
              </td>
              <td className="py-2 px-4 border-b">
                {editId === nivel.id ? (
                  <button 
                    onClick={() => handleSaveClick(nivel.id)}
                    className="bg-green-500 text-white px-4 py-1 rounded hover:bg-green-600"
                  >
                    Salvar
                  </button>
                ) : (
                  <>
                    <button 
                      onClick={() => handleEditClick(nivel)}
                      className="bg-blue-500 text-white px-4 py-1 rounded mr-2 hover:bg-blue-600"
                    >
                      Editar
                    </button>
                    <button 
                      onClick={() => onDelete(nivel.id)}
                      className="bg-red-500 text-white px-4 py-1 rounded hover:bg-red-600"
                    >
                      Excluir
                    </button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default NiveisList;
