// src/pages/NiveisPage.js
import React, { useState, useEffect } from 'react';
import NiveisForm from '../components/NiveisNiveisForm';
import NiveisList from '../components/NiveisList';
import { fetchNiveis, addNivel, updateNivel, deleteNivel } from '../api/api';

function NiveisPage() {
  const [niveis, setNiveis] = useState([]);
  const [editNivel, setEditNivel] = useState(null);

  useEffect(() => {
    const getNiveis = async () => {
      const niveisData = await fetchNiveis();
      setNiveis(niveisData);
    };

    getNiveis();
  }, []);

  const handleAddNivel = async (nivel) => {
    try {
      if (editNivel) {
        const updatedNivel = await updateNivel(editNivel.id, nivel);
        setNiveis(niveis.map(n => (n.id === editNivel.id ? updatedNivel : n)));
        setEditNivel(null);
      } else {
        const novoNivel = await addNivel(nivel);
        setNiveis([...niveis, novoNivel]);
      }
    } catch (error) {
      console.error('Erro ao adicionar ou editar nível:', error);
    }
  };

  const handleEditClick = (nivel) => {
    setEditNivel(nivel);
  };

  const handleDeleteNivel = async (id) => {
    try {
      await deleteNivel(id);
      setNiveis(niveis.filter(n => n.id !== id));
    } catch (error) {
      console.error('Erro ao remover nível:', error);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <NiveisForm onSubmit={handleAddNivel} editNivel={editNivel} />
      <NiveisList niveis={niveis} onEditClick={handleEditClick} onDelete={handleDeleteNivel} />
    </div>
  );
}

export default NiveisPage;
